import { useState, useRef, useEffect } from "react";
import Navbar from "../Navbar/Navbar";
import Footer from "../Footer/Footer";
import "../Home/Home.css";
import "./StudyInterface.css";

const StudyTracker = () => {

    const [showAchievements, setShowAchievements] = useState(false);

    const toggleAchievements = () => {
        setShowAchievements((prev) => !prev);
    };

    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState("");
    const [showInput, setShowInput] = useState(false);

    const addTask = () => {
        if (newTask.trim() !== "") {
        setTasks([...tasks, newTask]);
        setNewTask(""); // clears input
        }
    };
    return (

        <div className="tracker-page-container">
            <Navbar />

            <div className="dashboard">
                <div className ="sidebar">
                    <div class="header">
                        To-Do List
                        <button className="add-task-button" onClick={() => setShowInput(!showInput)}>+</button>
                    </div>
                    
                    {showInput && (
                        <div className="todo-input">
                        <input
                            type="text"
                            placeholder="Add a task..."
                            value={newTask}
                            onChange={(e) => setNewTask(e.target.value)}
                        />
                        <button onClick={addTask}>Add</button>
                        </div>
                    )}

                    <ul className="todo-list">
                        {tasks.map((task, index) => (
                        <li key={index}>{task}</li>
                        ))}
                    </ul>
                </div>

                <div className="middle-column">
                    <div className="image-container">
                        {/* You can insert an image later here */}
                    </div>
                    <div className="achievements-bar" onClick={toggleAchievements}>
                        Achievements + Statistics
                    </div>
                    {showAchievements && (
                        <div className="achievements-content">
                        {/* Replace this database content later */}
                        <p>‚≠ê You completed 3 Pomodoros today!</p>
                        <p>üìà Your focus time increased by 12%</p>
                        </div>
                    )}
                </div>    

                <div className="widgets">

                <div class="widget calendar-widget">
                    <div class="header">Calendar</div>
                </div>

                <div class="widget pomodoro-widget">
                    <div class="header">Pomodoro</div>
                </div>
                
                
            </div>
            </div>

            <div class="main">
            </div>


            <Footer />

        </div>
    );
};

export default StudyTracker;
