import { useState, useRef, useEffect } from "react";
import Navbar from "../Navbar/Navbar";
import Footer from "../Footer/Footer";
import "../Home/Home.css";
import "./StudyTracker.css";

const StudyTracker = () => {
    
    useEffect(() => {
        const footer = document.getElementById('footer');
        const footerHeader = document.getElementById('footerHeader');

        footerHeader.addEventListener('click', () => {
            footer.classList.toggle('collapsed');
        });

        // Clean up the event listener when component unmounts
        return () => {
            footerHeader.removeEventListener('click', () => {
                footer.classList.toggle('collapsed');
            });
        };
    }, []);

    return (
        <div className="tracker-page-container">
            <Navbar />

            <div className="dashboard">
                {/* Sidebar */}
                <div className="sidebar">
                    <div className="header">To-Do List</div>
                </div>

                {/* Main */}
                <div className="main">
                    <img src="blank" alt="bolCover" className="placeholder" id="placeholder" />
                    
                    {/* Collapsible Footer */}
                    <div className="footer collapsible collapsed" id="footer">
                        <div className="footer-header" id="footerHeader">Achievements + Statistics</div>
                        <div className="footer-content" id="footerContent">
                            Content goes here later =u=
                        </div>
                    </div>
                </div>

                {/* Widgets */}
                <div className="widgets">
                    <div className="widget">
                        <div className="header">Calendar</div>
                    </div>
                    <div className="widget">
                        <div className="header">Pomodoro</div>
                    </div>
                </div>
            </div>

            <Footer />
        </div>
    );
};

export default StudyTracker;
